import React, { Component, StrictMode } from "react";
import PersonList from './PersonList';
import Person from './Person';


class Application extends Component {

    
    state = { 

        people: [
            <Person key="1" value="1" onAddressChange = {()=>this.handleAddressChange}></Person>,
            <Person key="2" value="2" onAddressChange = {()=>this.handleChange}></Person>,
            <Person key="3" value="3" onAddressChange = {()=>this.handleChange}></Person>,
          ],
        addressess: [
            "","",""
        ]
        

     }


    handleAddressChange = (evt, val) => {
        console.log("WORKING");
        let ev = evt.target.value;
        let newAddresses = this.state.addressess;
        newAddresses[val] = ev;
        this.setState({addresses:newAddresses})
    }
    

    handleAddPerson = () => {

        this.setState({
          people: [
            ...this.state.people,
            <Person
              key={this.state.people.length + 1}
              value={this.state.people.length + 1}
              address=""
            ></Person>,
          ],
        });
      };

    handleDeletePerson = () => {
        let people = this.state.people.slice(0, -1);
        this.setState({
          people: people,
        });
      };

    propChecker = () => {
        let b = this.state.people
        b[0].props.address = "LOL"
        this.setState({people:b})
        //console.log(this.state.people[0].props.address)

    }

    render() { 
        return ( 
            <div>
                <h1>Linq</h1>
                <PersonList
                people = {this.state.people} 
                onAddPerson = {()=>this.handleAddPerson()}
                onDeletePerson = {()=>this.handleDeletePerson()}></PersonList>

                <button onClick={this.propChecker}>Calculate</button>
                
            </div>
         );
    }
}
 
export default Application;
