import React, { Component } from "react";



class Person extends Component {
    state = { val: "" }


    handleChange = (evt) => {
        let fff = this.state.val;
        fff = evt.target.value;
        this.setState({val:fff})
    }

    render() { 
        return ( 

                <div>
                    <h4>Person #{this.props.value}</h4>
                    <form>
                        <label htmlFor="">Address</label>
                        <input type="text"value = {this.state.val[0]} onChange={(evt) => this.handleChange(evt)}/>
                        <br/>
                    </form>
                </div>
            );
    }
}
 
export default Person;
