import React, { Component } from 'react';
import Person from './Person';



class PersonList extends Component {
    state = { 

        people: []

        
     }

    renderPerson = i => {
        return <Person value={i}></Person>
    } 

    handleAdd = () => {
        this.setState({
            people: [...this.state.people,<Person value={this.state.people.length}></Person>]
        })


    }

    


    render() { 

        

        return ( 

            <div>

                <button size='lg' onClick={this.handleAdd}>Add Person</button>
                
                
                {this.state.people}
            </div>

         );
    }
}
 
export default PersonList;
