import React, { Component } from "react";


class PlacePicker extends Component {

    state = {
        sliderValue: "300"
    }

    sliderChange = (e) => {

        this.setState({sliderValue:e})
        this.props.onSetRadius(e)

    }


  render() {
    return (
      <div>
        <span>
          <h4><span className="badge badge-info">Point of Interest</span></h4>
          <input style={{width:"400px"}} className="form-control" type="test" placeholder="Leave Blank If None" onChange={(e)=>this.props.onSetPOI(e.target.value)}></input>
        </span>
        <span>
          <h4>Radius</h4>
          <input
            type="range"
            min="0"
            max="1000"
            value={this.state.sliderValue}
            onChange={(e)=>this.sliderChange(e.target.value)}
          ></input>
        </span>
      </div>
    );
  }
}

export default PlacePicker;
