import React, { Component } from "react";
import { Map, Marker, GoogleApiWrapper, InfoWindow } from "google-maps-react";




export class MapContainer extends Component {


  state = {
    showingInfoWindow: false,
    activeMarkers: {},
    selectedPlace: {}
  };


  displayMarkers = () => {
    return this.props.coords.map((address, index) => {
      return (
        <Marker
          key={index}
          id={index}
          position={{
            lat: address.lat,
            lng: address.lng,
          }
        
        }
        />
      );
    });
  };

  onMarkerClick = (props, marker, e) =>
  this.setState({
    selectedPlace: props,
    activeMarker: marker,
    showingInfoWindow: true
  });

onClose = props => {
  if (this.state.showingInfoWindow) {
    this.setState({
      showingInfoWindow: false,
      activeMarker: null
    });
  }
};


  render() {
    

    return (
      <Map
        google={this.props.google}
        zoom={8}
        style={{
          width: "40%",
          height: "50%",
        }}
        initialCenter={{
          lat: this.props.currentLocation[0],
          lng: this.props.currentLocation[1],
        }}
      >
        {this.displayMarkers()}
        <Marker
          key="midpoint"
          id="midpoint"
          position={{
            lat: this.props.midpoint[0],
            lng: this.props.midpoint[1],
          }}
          icon={{
            url: "http://maps.google.com/mapfiles/ms/icons/green-dot.png",
          }}
        ></Marker>
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyAF6LzDWnCO0yQ3_xVfXMYicN6MqUFl4q0",
})(MapContainer);
