import React, { Component } from 'react';
import Person from './Person';



class PersonList extends Component {
    state = { 

        people: [
            {id:1, value: null},
            {id:2, value: null},
            {id:3, value: null},

        ]
     }

    renderPerson (i) {
        return <Person value={i}/>;
    } 

    render() { 
        return ( 

            <div>
                renderPerson(1);
                renderPerson(2);
                renderPerson(3);
                



                <button size='lg' onClick={this.handleAdd}>Add Person</button>

            </div>

         );
    }
}
 
export default PersonList;
