import React, { Component, StrictMode } from "react";
import PersonList from './PersonList';
import Person from './Person';


class Application extends Component {


    constructor(props) {
        super(props);
        this.state = { 

            people: [
                <Person key="1" value="1" addresses={this.addresses}></Person>,
                <Person key="2" value="2" addresses={this.addresses}></Person>,
                <Person key="3" value="3" addresses={this.addresses}></Person>,
              ],
            
            addresses: [
                '','',''
            ]
    
         }


    }
    state = { 

        people: [
            <Person key="1" value="1" addresses={this.addresses}></Person>,
            <Person key="2" value="2" addresses={this.addresses}></Person>,
            <Person key="3" value="3" addresses={this.addresses}></Person>,
          ],
        
        addresses: [
            '','',''
        ]

     }


    handleAddPerson = () => {

        this.setState({
          people: [
            ...this.state.people,
            <Person
              key={this.state.people.length + 1}
              value={this.state.people.length + 1}
              addresses={this.state.addresses}
            ></Person>,
          ],
        });
      };

    handleDeletePerson = () => {
        let people = this.state.people.slice(0, -1);
        this.setState({
          people: people,
        });
      };



    render() { 
        return ( 
            <div>
                <h1>Linq</h1>
                <PersonList
                people = {this.state.people} 
                onAddPerson = {()=>this.handleAddPerson()}
                onDeletePerson = {()=>this.handleDeletePerson()}></PersonList>

                <button>Calculate</button>
                
            </div>
         );
    }
}
 
export default Application;
