import React, { Component } from "react";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Button from "react-bootstrap/Button";
import ToggleButton from "react-bootstrap/ToggleButton";
import './icons.css';

class DirectionsPicker extends Component {


    state = {
        checked: null
    }
  render() {



    const radios = [
        {name: 'directions_bike'},
        {name: 'directions_bus'},
        {name: 'directions_car'},
        {name: 'directions_walk'}
    ]

    
    return (

    <div>
        <h4>Directions specifications</h4>

        <ButtonGroup toggle>
        {radios.map((radio, idx) => (
          <ToggleButton
            key={idx}
            type="radio"
            variant="secondary"
            name="radio"
            value={radio.name}
            checked={radio.name === this.state.checked}
            onChange={(e)=>this.setState({checked:e.currentTarget.name})}
          >
            <i class="material-icons">{radio.name}</i>
          </ToggleButton>
        ))}
      </ButtonGroup>
      </div>
    );
  }
}

export default DirectionsPicker;
